mapper.update:
UPDATE TBL_MEMBER
SET MEMBER_PWD = #{memberPwd}
WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT * FROM ( SELECT rownum as RNUM, A.* FROM ( SELECT MEMBER_SEQ, MEMBER_ID, MEMBER_NAME, REG_TYPE, PHONE, GENDER_TYPE, REG_DATE, DEL_FLAG, DEL_DATE FROM TBL_MEMBER Where 1=1
order by REG_DATE desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
UPDATE TBL_MEMBER 
SET del_flag = #{type}
,del_date=null
,del_date=sysdate
WHERE member_seq IN

	#{memberSeq}

update tbl_member
set MEMBER_PWD=#{memberPwd}, MEMBER_NAME=#{memberName}, ZIP_CODE=#{zipCode}, 
ADDRESS=#{address}, DETAIL_ADDRESS=#{detailAddress}, EXTRA_ADDRESS=#{extraAddress}, 
PHONE=#{phone},	GENDER_TYPE=#{genderType}, BIRTHDAY=#{birthday}, PROFILE_IMG=#{profileImg} , 
PROFILE_RENAMEIMG=#{profileRenameimg}
where MEMBER_ID=#{memberId}

update tbl_member
set DEL_DATE=sysdate, DEL_FLAG='Y' 
where MEMBER_ID=#{memberId}
