mapper.select:
select * from ( select rownum as RNUM, A.* from ( select a.board_seq as boardSeq, a.board_type as boardType, a.board_title as boardTitle, a.board_contents as boardContents, a.hits as hits, a.reg_date as regDate, a.ref_memberseq as refMemberseq, b.member_id as memberId, b.member_name as memberName from tbl_board a join tbl_member b on a.ref_memberseq=b.member_seq where board_type=#{searchCondition}
order by board_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

 	select tb.board_seq, tm.member_name as memberName from 
	tbl_board tb join tbl_member tm 
	on tb.REF_MEMBERSEQ = tm.MEMBER_SEQ where board_seq=#{boardSeq}

	 	select  B."ROWNUM" as RNUM, B.REG_DATE as regDate  
        from(
			select A.BOARD_SEQ, rownum, A.REG_DATE 
			from(
				select * from tbl_board order by board_seq desc
			)A where rownum >0
		)B where B.BOARD_SEQ=#{boardSeq}

mapper.update:
 
	 	update tbl_board
	 	set board_title=#{boardTitle}, board_contents=#{boardContents}
	 	where board_seq=#{boardSeq}

update tbl_board
set hits=hits+1
where board_seq=#{boardSeq}

mapper.select:
select * from ( select rownum as RNUM, A.* from ( select TO_CHAR(SUBSTR(cm.COMMENT_DESC, 1, 300)) as COMMENTDESC, cm.COMMENT_SEQ as COMMENTSEQ, cm.REG_DATE as REGDATE, m.MEMBER_NAME as MEMBERNAME, m.PROFILE_RENAMEIMG as PHOTO, m.MEMBER_SEQ as MEMBERSEQ from tbl_comment cm join tbl_member m on cm.REF_MEMBERSEQ = m.MEMBER_SEQ where ref_boardseq=#{searchCondition}
order by comment_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
  	 	update tbl_comment
		set comment_desc=#{commentDesc}, reg_date=sysdate
		where comment_seq=#{commentSeq}
