mapper.select:
select * from ( select rownum as RNUM, A.* from ( select a.board_seq as boardSeq, a.board_type as boardType, a.board_title as boardTitle, a.board_contents as boardContents, a.hits as hits, a.reg_date as regDate, a.ref_memberseq as refMemberseq, b.member_id as memberId, b.member_name as memberName from tbl_board a join tbl_member b on a.ref_memberseq=b.member_seq where board_type=#{searchCondition}
order by board_seq desc )A ) where RNUM > #{firstRecordIndex}
and RNUM<= #{firstRecordIndex} + #{recordCountPerPage}

mapper.update:
 
	 	update tbl_board
	 	set board_title=#{boardTitle}, board_contents=#{boardContents}
	 	where board_seq=#{boardSeq}

update tbl_board
set hits=hits+1
where board_seq=#{boardSeq}
